mapper.select:
SELECT 
FROM 


            and goods_sn like concat('%', #{filter.goodsSn}, '%')


            and goods_type = #{filter.goodsType}


            and goods_name like concat('%', #{filter.goodsName}, '%')


            and goods_thumb like concat('%', #{filter.goodsThumb}, '%')


            and goods_img like concat('%', #{filter.goodsImg}, '%')


            and cat_id = #{filter.catId}


            and customer_cat_id = #{filter.customerCatId}


            and brand_id = #{filter.brandId}


            and supplier_id = #{filter.supplierId}


            and units_id = #{filter.unitsId}


            and factory_price = #{filter.factoryPrice}


            and supply_price = #{filter.supplyPrice}


            and market_price = #{filter.marketPrice}


            and retail_price = #{filter.retailPrice}


            and bottom_price_attr_id = #{filter.bottomPriceAttrId}


            and price_explain like concat('%', #{filter.priceExplain}, '%')


            and deliver_fee = #{filter.deliverFee}


            and moq = #{filter.moq}


            and sale_count = #{filter.saleCount}


            and exception_un_sale_num = #{filter.exceptionUnSaleNum}


            and gift_send_count = #{filter.giftSendCount}


            and warn_stock = #{filter.warnStock}


            and goods_spec like concat('%', #{filter.goodsSpec}, '%')


            and goods_param like concat('%', #{filter.goodsParam}, '%')


            and is_sale = #{filter.isSale}


            and goods_desc like concat('%', #{filter.goodsDesc}, '%')


            and goods_status = #{filter.goodsStatus}


            and goods_keywords like concat('%', #{filter.goodsKeywords}, '%')


            and score = #{filter.score}


            and gd_group_id = #{filter.gdGroupId}


            and activity_price = #{filter.activityPrice}


            and favorite_count = #{filter.favoriteCount}


            and comment_count = #{filter.commentCount}


            and good_comment_count = #{filter.goodCommentCount}


            and good_comment_rate = #{filter.goodCommentRate}


            and default_activity_quota_number = #{filter.defaultActivityQuotaNumber}


            and audit_status = #{filter.auditStatus}


            and delete_cord___ = #{filter.deleteCord___}


            and is_new = #{filter.isNew}


            and is_best = #{filter.isBest}


            and is_hot = #{filter.isHot}


            and status_remarks like concat('%', #{filter.statusRemarks}, '%')


            and audit_pass_time = #{filter.auditPassTime}


            and attr_cat_id = #{filter.attrCatId}


            and goods_stock = #{filter.goodsStock}


            and goods_code like concat('%', #{filter.goodsCode}, '%')


            and is_book = #{filter.isBook}


            and rebate = #{filter.rebate}


            and book_quantity = #{filter.bookQuantity}


            and is_recomm = #{filter.isRecomm}


            and is_admin_best = #{filter.isAdminBest}


            and is_admin_recom = #{filter.isAdminRecom}


            and is_shop_recomm = #{filter.isShopRecomm}


            and is_index_recomm = #{filter.isIndexRecomm}


            and is_activity_recomm = #{filter.isActivityRecomm}


            and is_inner_recomm = #{filter.isInnerRecomm}


            and recomm_desc like concat('%', #{filter.recommDesc}, '%')


            and sale_time = #{filter.saleTime}


            and delivery_fare_temp_id = #{filter.deliveryFareTempId}


            and delivery_fare_temp_value like concat('%', #{filter.deliveryFareTempValue}, '%')


            and create_time = #{filter.createTime}


            and update_time = #{filter.updateTime}

    and is_delete = 0

            and version_id = #{filter.versionId}


ORDER BY updateTime DESC

